import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import Container from 'react-bootstrap/esm/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Dropdown from 'react-bootstrap/Dropdown';
import CleanTextBox from './CleanTextBox.js';

function BuyForm(props) {
    return (
        <div>
            <Form.Group style={{border}} className="mb-3" controlId="formBasicPayment">
                <CleanTextBox></CleanTextBox>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formConversion">
                <Alert key={'success'} variant={'success'}>
                    <Alert.Heading>You Get:</Alert.Heading>
                    <Container>
                        <Row>
                            <Col>
                                <p style={{fontSize : 30}}>{props.formData.Payment}</p>
                            </Col>
                            <Col>
                                <Dropdown>
                                    <Dropdown.Toggle style={{fontSize: 30}} variant="success" id="dropdown-basic">
                                        USD Reg
                                    </Dropdown.Toggle>
                                    <Dropdown.Menu>
                                        <Dropdown.Item href="#/action-1">USDC Avalanche</Dropdown.Item>
                                        <Dropdown.Item href="#/action-2">Other USDC</Dropdown.Item>
                                        <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                                    </Dropdown.Menu>
                                </Dropdown>
                            </Col>
                        </Row>
                    </Container>
                </Alert>
            </Form.Group>
        </div>
    );
}
export default BuyForm;
