import Form from 'react-bootstrap/Form';
import Alert from 'react-bootstrap/Alert';
import Container from 'react-bootstrap/esm/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Dropdown from 'react-bootstrap/Dropdown';

function BuyForm(props) {
    return (
        <div>
            <Form.Group className="mb-3" controlId="formBasicPayment">
                <Alert key={'danger'} variant={'danger'}>
                        <Alert.Heading>You Pay:</Alert.Heading>
                        <Container>
                            <Row>
                                <Col xs={7}>
                                    <Form.Control style={{fontSize: 30}} size="lg" type="number" id="Payment" name="Payment" placeholder="0.00" value={props.Payment} onChange={props.handleChange}/>
                                </Col>
                                <Col>
                                </Col>
                            </Row>
                        </Container>
                </Alert>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formConversion">
                <Alert key={'success'} variant={'success'}>
                    <Alert.Heading>You Get:</Alert.Heading>
                    <p style={{fontSize : 30}}>{props.Payment} USDC</p>
                </Alert>
            </Form.Group>
        </div>
    );
}
export default BuyForm;
