import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useState } from "react";
import logo from './AvenirBlack.svg';
import { motion } from "framer-motion";
import Form from 'react-bootstrap/Form';
import Container from 'react-bootstrap/esm/Container';
import Alert from 'react-bootstrap/Alert';

function Page(props) {
    const [formData, setFormData] = useState({Payment : "", Name: "", Wallet: ""});  
    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
    };

    const handleNext = (event) => {
        event.preventDefault();
        alert(`You Paid: ${formData.Payment}, Name: ${formData.Name}, WalletNumber: ${formData.Wallet}`);
        props.setPage(props.page + 1); 
        props.setX(1000);
        props.setPage(props.page + 1);
    };

    return (
        <Card border={'success '} bg={'light'} text={'dark'} style={{height: "45rem", width: "40rem"}}>
            <Card.Img src={logo} alt="logo" style={{width: "30%"}}/>
            <br />
            <Card.Body>
                <Card.Title>Crypto Convertor!</Card.Title>
                <br />
                        <Form>
                            <Form.Group className="mb-3" controlId="formBasicPayment">
                                <Alert key={'failure'} variant={'success'}>
                                    <Alert.Heading>Hey, nice to see you</Alert.Heading>
                                    <p size={{fontSize : 20}}>{formData.Payment}</p>
                                </Alert>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formConversion">
                                <Alert key={'success'} variant={'success'}>
                                    <Alert.Heading>Hey, nice to see you</Alert.Heading>
                                    <p size={{fontSize : 20}}>{formData.Payment}</p>
                                </Alert>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formName">
                                <Form.Label>Name:</Form.Label>
                                <Form.Control type="text" id="Name" name="Name" placeholder="Enter Name" value={formData.Name} onChange={handleChange}/>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formWallet">
                                <Form.Label>Wallet:</Form.Label>
                                <Form.Control type="number" id="Wallet" name="Wallet" placeholder="Enter Wallet" value={formData.Wallet} onChange={handleChange}/>
                            </Form.Group>
                            <Button variant="primary" onClick={handleNext}>Next!</Button>
                        </Form>
            </Card.Body>
        </Card>
    );
}

export default Page;
