import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useState } from "react";

function Form() {
    const [formData, setFormData] = useState({Payment : "", Name: "", Email: "", WalletNu});  
    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        alert(`You Paid: ${formData.Payment}, Name: ${formData.Name}, 
            Email: ${formData.Email}, WalletNumber: ${formData.WalletNumber}`);
    };


    return (
        <Card bg={'light'} text={'dark'} style={{height: "50rem", width: "40rem"}}>
        <Card.Body>
            <Card.Title>Crypto Convertor!</Card.Title>
            <br />
                    <form onSubmit={handleSubmit}>
                        <Card.Text>You Pay:</Card.Text>
                        <input type="number" id="Payment" name="Payment" value={formData.Payment} onChange={handleChange}/>
                        <br />

                        <Card.Text>You Get: {formData.Payment * 2}</Card.Text>
                        <br />

                        <Card.Text>Name:</Card.Text>
                        <input type="text" id="Name" name="Name" value={formData.Name} onChange={handleChange}/>
                        <br />

                        <Card.Text>Email:</Card.Text>
                        <input type="text" id="Email" name="Email" value={formData.Email} onChange={handleChange}/>
                        <br />

                        <Card.Text>Wallet Number:</Card.Text>
                        <input type="number" id="Wallet" name="Wallet" value={formData.WalletNumber} onChange={handleChange}/>
                        <br />
                        <Button type="Submit" variant="primary">Submit</Button>
                    </form>
        </Card.Body>
        </Card>
    );
}

export default Form;
