import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useState } from "react";

function Form() {
    const [formData, setFormData] = useState({Payment : "", Conversion : ""});  
    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        alert(`You Paid: ${formData.Payment}, Message: ${formData.Conversion}`
        );
    };


    return (
        <Card style={{ display: "flex", alignItems: "center", justifyContent: "center",height: "100%",  }}>
        <Card.Img variant="top" src="holder.js/100px180" />
        <Card.Body>
            <Card.Title>Card Title</Card.Title>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="Payment">You Pay:</label>
                    <input type="number" id="Payment" name="Payment" value={formData.Payment} onChange={handleChange}/>

                    <label htmlFor="Conversion">You Get:</label>
                    <input type="number" id="Conversion" name="Conversion" value={formData.Conversion} onChange={handleChange}/>

                    <Button type="Submit" variant="primary">Submit</Button>
                </form>
        </Card.Body>
        </Card>
    );
}

export default Form;
