import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useState } from "react";
import logo from './AvenirBlack.svg';
import { motion } from "framer-motion";
import Form from 'react-bootstrap/Form';
import Container from 'react-bootstrap/esm/Container';
import Alert from 'react-bootstrap/Alert';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Dropdown from 'react-bootstrap/Dropdown';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';

function Page(props) {
    const [buy, setBuy] = useState(true);
    const [formData, setFormData] = useState({Payment : "", Name: "", Wallet: ""});  
    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
    };

    const handleNext = (event) => {
        event.preventDefault();
        alert(`You Paid: ${formData.Payment}, Name: ${formData.Name}, WalletNumber: ${formData.Wallet}`);
        props.setPage(props.page + 1); 
        props.setX(1000);
        props.setPage(props.page + 1);
    };

    const handleBuy = () => setBuy(!buy);

    return (
        <Card border={'success '} bg={'light'} text={'dark'} style={{height: "45rem", width: "40rem"}}>            
            <Card.Body>
                <Container>
                    <Row>
                        <Col>
                            <Card.Img src={logo} alt="logo" style={{width: "80%"}}/>
                        </Col>
                        <Col>
                            <ButtonGroup>
                                <Button variant={buy ? 'success' : 'outline-danger'} onClick={handleBuy}>Buy</Button>
                                <Button variant={buy ? 'outline-danger' : 'success'} onClick={handleBuy}>Sell</Button>
                            </ButtonGroup>
                        </Col>
                    </Row>
                </Container>
                <br />
                        <Form>

                            <Form.Group className="mb-3" controlId="formName">
                                <Form.Label>Name:</Form.Label>
                                <Form.Control type="text" id="Name" name="Name" placeholder="Enter Name" value={formData.Name} onChange={handleChange}/>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formWallet">
                                <Form.Label>Wallet:</Form.Label>
                                <Form.Control type="number" id="Wallet" name="Wallet" placeholder="Enter Wallet" value={formData.Wallet} onChange={handleChange}/>
                            </Form.Group>
                            <Button variant="primary" onClick={handleNext}>Next!</Button>
                        </Form>
            </Card.Body>
        </Card>
    );
}

export default Page;
