import { useEffect } from 'react';
import { RampInstantSDK } from '@ramp-network/ramp-instant-sdk';

const Page2 = () => {
    useEffect(() => {
      const rampContainer = document.getElementById('ramp-container');

      const rampInstantSDK = new RampInstantSDK({
        hostAppName: 'Your App',
        hostLogoUrl: 'https://assets.ramp.network/misc/test-logo.png',
        hostApiKey: 'your_host_api_key',
        variant: 'embedded-desktop',
        containerNode: rampContainer,
      });

      rampInstantSDK.show();

      return () => {
        // Cleanup code if necessary
        rampInstantSDK.hide();
      };
    }, []);

    return <div id="ramp-container" />;
  };

  export default Page2;
