import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useState } from "react";
import logo from './AvenirBlack.svg';
import { motion } from "framer-motion";
import Form from 'react-bootstrap/Form';
import Container from 'react-bootstrap/esm/Container';
import Alert from 'react-bootstrap/Alert';

function Page(props) {
    const [formData, setFormData] = useState({Payment : "", Name: "", Wallet: ""});  
    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
    };

    const handleNext = (event) => {
        event.preventDefault();
        alert(`You Paid: ${formData.Payment}, Name: ${formData.Name}, WalletNumber: ${formData.Wallet}`);
        props.setPage(props.page + 1); 
        props.setX(1000);
        props.setPage(props.page + 1);
    };

    return (
        <Card border={'success '} bg={'light'} text={'dark'} style={{height: "50rem", width: "40rem"}}>
            <Card.Img src={logo} alt="logo" style={{width: "30%"}}/>
            <br />
            <Card.Body>
                <Card.Title>Crypto Convertor!</Card.Title>
                <br />
                        <Form>
                            <Form.Group className="mb-3" controlId="formBasicPayment">
                                <Form.Label>You Pay:</Form.Label>
                                <Form.Control size="lg" as="textarea" rows={3} type="number" id="Payment" name="Payment" placeholder="0.00" value={formData.Payment} onChange={handleChange}/>
                            </Form.Group>

                            <Alert key={'variant'} variant={variant}>This is a {variant} alertâ€”check it out!</Alert>

                            <Form.Group className="mb-3" controlId="formName">
                                <Form.Label>Name:</Form.Label>
                                <Form.Control type="text" id="Name" name="Name" placeholder="Enter Name" value={formData.Payment} onChange={handleChange}/>
                            </Form.Group>

                            <Button variant="primary" onClick={handleNext}>Next!</Button>
                        </Form>
            </Card.Body>
        </Card>
    );
}

export default Page;
