import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useState } from "react";
import logo from './AvenirBlack.svg';
import { motion } from "framer-motion";
import Form from 'react-bootstrap/Form';

function Page(props) {
    const [formData, setFormData] = useState({Payment : "", Name: "", Email: "", Wallet: ""});  
    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
    };

    const handleNext = (event) => {
        event.preventDefault();
        alert(`You Paid: ${formData.Payment}, Name: ${formData.Name}, 
            Email: ${formData.Email}, WalletNumber: ${formData.Wallet}`);
        props.setPage(props.page + 1); 
        props.setX(1000);
        props.setPage(props.page + 1);
    };

    return (
        <Card border={'success '} bg={'light'} text={'dark'} style={{height: "50rem", width: "40rem"}}>
            <Card.Img src={logo} alt="logo" style={{width: "30%"}}/>
            <br />
            <Card.Body>
                <Card.Title expand="lg">Crypto Convertor!</Card.Title>
                <br />
                        <Form>
                            <Form.Group className="mb-3" controlId="formBasicEmail">
                                <Form.Label>You Pay:</Form.Label>
                                <Form.Control type="email" placeholder="Enter email" />
                            </Form.Group>

                            <Card.Text>You Pay:</Card.Text>
                            <input type="number" id="Payment" name="Payment" value={formData.Payment} onChange={handleChange}/>
                            <br />

                            <Card.Text>You Get: {formData.Payment * 2}</Card.Text>
                            <br />

                            <Card.Text>Address:</Card.Text>
                            <input type="text" id="Name" name="Name" value={formData.Name} onChange={handleChange}/>
                            <br />

                            <Card.Text>Email:</Card.Text>
                            <input type="text" id="Email" name="Email" value={formData.Email} onChange={handleChange}/>
                            <br />

                            <Card.Text>Wallet Number:</Card.Text>
                            <input type="number" id="Wallet" name="Wallet" value={formData.Wallet} onChange={handleChange}/>
                            <br />

                            <Button variant="primary" onClick={handleNext}>Next!</Button>
                        </Form>
            </Card.Body>
        </Card>
    );
}

export default Page;
