import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import logo from './AvenirBlack.svg';
import { motion } from "framer-motion";

const Form = ({ page, setPage, formData, setFormData, x }) => {

    const handleNext = (event) => {
        event.preventDefault();
        alert(`You Paid: ${formData.Payment}, Name: ${formData.Name}, 
            Email: ${formData.Email}, WalletNumber: ${formData.Wallet}`);
        setPage(page + 1); setX(1000);
        setPage(page + 1);
    };

    return (
        <motion.div                            
          initial={{ x: x }}
          transition={{ duration: 1 }}
          animate={{ x: 0 }}
        >
            <Card border={'success '} bg={'light'} text={'dark'} style={{height: "50rem", width: "40rem"}}>
                <Card.Img src={logo} alt="logo" style={{width: "30%"}}/>
                <br />
                <Card.Body>
                    <Card.Title expand="lg">Crypto Convertor!</Card.Title>
                    <br />
                            <form>
                                <Card.Text>You Pay:</Card.Text>
                                <input type="number" id="Payment" name="Payment" value={formData.Payment} 
                                onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}/>
                                <br />

                                <Card.Text>You Get: {formData.Payment * 2}</Card.Text>
                                <br />

                                <Card.Text>Name:</Card.Text>
                                <input type="text" id="Name" name="Name" value={formData.Name} 
                                onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}/>
                                <br />

                                <Card.Text>Email:</Card.Text>
                                <input type="text" id="Email" name="Email" value={formData.Email} 
                                onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}/>
                                <br />

                                <Card.Text>Wallet Number:</Card.Text>
                                <input type="number" id="Wallet" name="Wallet" value={formData.Wallet} 
                                onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}/>
                                <br />

                                <Button variant="primary" onClick={handleNext}>Next!</Button>
                            </form>
                </Card.Body>
            </Card>
        </motion.div>
    );
}

export default Form;
